import React, { useState } from 'react';
import { View, Text, Image, StyleSheet, ScrollView, TextInput, Button } from 'react-native';

const initialProfile = {
  name: 'Alex Johnson',
  avatar: require('@/assets/images/user_avatar.png'),
  bio: 'Nature enthusiast and photographer. Love to explore new landscapes and capture the beauty of life.',
  country: 'Canada',
  languages: ['English', 'French', 'Spanish'],
  interests: ['Photography', 'Hiking', 'Bird Watching', 'Traveling'],
  posts: [
    { id: '1', title: 'Autumn Adventures', image: 'data:image/jpeg;base64,' },
    { id: '2', title: 'Summer Beach Fun', image: 'data:image/jpeg;base64,' },
  ],
};

export default function UserProfile() {
  const [profile, setProfile] = useState(initialProfile);
  const [newInterest, setNewInterest] = useState('');
  const [newLanguage, setNewLanguage] = useState('');

  const addInterest = () => {
    if (newInterest && !profile.interests.includes(newInterest)) {
      setProfile({...profile, interests: [...profile.interests, newInterest]});
      setNewInterest('');
    }
  };

  const addLanguage = () => {
    if (newLanguage && !profile.languages.includes(newLanguage)) {
      setProfile({...profile, languages: [...profile.languages, newLanguage]});
      setNewLanguage('');
    }
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.profileContainer}>
        <Image source={profile.avatar} style={styles.profileImage} />
        <Text style={styles.profileName}>{profile.name}</Text>
        <Text style={styles.profileBio}>{profile.bio}</Text>
        <Text style={styles.profileDetails}>{profile.country} Â· {profile.languages.join(', ')}</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Interests</Text>
        <View style={styles.interestsContainer}>
          {profile.interests.map((interest, index) => (
            <Text key={index} style={styles.interest}>{interest}</Text>
          ))}
        </View>
        <View style={styles.inputContainer}>
          <TextInput
            style={styles.input}
            onChangeText={setNewInterest}
            value={newInterest}
            placeholder="Add new interest..."
            placeholderTextColor="#888"
          />
          <Button title="Add" onPress={addInterest} color="#007BFF" />
        </View>
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Languages</Text>
        <View style={styles.languagesContainer}>
          {profile.languages.map((language, index) => (
            <Text key={index} style={styles.language}>{language}</Text>
          ))}
        </View>
        <View style={styles.inputContainer}>
          <TextInput
            style={styles.input}
            onChangeText={setNewLanguage}
            value={newLanguage}
            placeholder="Add new language..."
            placeholderTextColor="#888"
          />
          <Button title="Add" onPress={addLanguage} color="#007BFF" />
        </View>
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Posts</Text>
        <View style={styles.postsContainer}>
          {profile.posts.map((post, index) => (
            <View key={index} style={styles.postCard}>
              <Image source={{ uri: post.image }} style={styles.postImage} />
              <Text style={styles.postTitle}>{post.title}</Text>
            </View>
          ))}
        </View>
      </View>
    </ScrollView>
  );
}

